const buttons=document.querySelector(".buttons"),count=document.querySelector(".count"),reset=document.querySelector("button.reset"),add=document.querySelector(".add"),sub=document.querySelector(".subtract");buttons.addEventListener("click",e=>{e.target.classList.contains("add")&&(count.innerHTML++,setColor()),e.target.classList.contains("subtract")&&(count.innerHTML--,setColor()),e.target.classList.contains("reset")&&(count.innerHTML=0,setColor())});const setColor=()=>{let value=count.innerHTML;count.style.color=value>0?"green":value<0?"red":"white"};